/**
 * Fallback images for when API requests fail
 */

// Placeholder image URLs - these are base64 encoded tiny placeholders
// In a real application, you would use actual image paths
export const categoryPlaceholders = [
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
    "data:image/svg+xml;base64,",
];

// Default category names to use if API doesn't return names
export const categoryNames = [
    "Electronics",
    "Clothing",
    "Furniture",
    "Sports",
    "Books",
    "Grocery",
    "Beauty",
    "Toys",
    "Pets",
    "Art"
];

// Function to get a fallback category with both image and name
export const getFallbackCategory = (index) => {
    const normalizedIndex = index % categoryPlaceholders.length;
    return {
        image: categoryPlaceholders[normalizedIndex],
        name: categoryNames[normalizedIndex],
        id: `fallback-${index}`
    };
};

// Generate a set of fallback categories
export const generateFallbackCategories = (count = 10) => {
    return Array.from({ length: count }, (_, index) => getFallbackCategory(index));
}; 